package javaBasics;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 4;
		
		if(a <= 5) {
			System.out.println("a is smaller number");
		} else if (a>5 && a<=10){
			System.out.println("a is medium size number");
		} else {
			System.out.println("a is larger number");
		}
	}
}
